var target = entity.modelAccessor;

// Explicitly importing Java ArrayList
var ArrayList = Java.type("java.util.ArrayList");

// Mapping scalar fields
target.university = {
    universityId: input.higherEducationInstitution.institutionId != null ? input.higherEducationInstitution.institutionId : "",
    name: input.higherEducationInstitution.institutionName != null ? input.higherEducationInstitution.institutionName : "",
    establishedYear: input.higherEducationInstitution.foundedYear != null ? input.higherEducationInstitution.foundedYear : 0,
    location: {
        address: input.higherEducationInstitution.campus.street != null ? input.higherEducationInstitution.campus.street : "",
        city: input.higherEducationInstitution.campus.town != null ? input.higherEducationInstitution.campus.town : "",
        state: input.higherEducationInstitution.campus.state != null ? input.higherEducationInstitution.campus.state : "",
        zipCode: input.higherEducationInstitution.campus.postalCode != null ? input.higherEducationInstitution.campus.postalCode : ""
    },
    departments: new ArrayList(),
    faculties: new ArrayList(),
    students: new ArrayList()
};

// Handling academic departments
if (input.higherEducationInstitution.academicDepartments != null) {
    for (var i = 0; i < input.higherEducationInstitution.academicDepartments.length; i++) {
        var deptInput = input.higherEducationInstitution.academicDepartments[i];
        var department = {
            departmentId: deptInput.departmentIdentifier != null ? deptInput.departmentIdentifier : "",
            departmentName: deptInput.departmentTitle != null ? deptInput.departmentTitle : "",
            head: {
                facultyId: deptInput.departmentHead.facultyIdentifier != null ? deptInput.departmentHead.facultyIdentifier : "",
                firstName: deptInput.departmentHead.givenName != null ? deptInput.departmentHead.givenName : "",
                lastName: deptInput.departmentHead.surname != null ? deptInput.departmentHead.surname : "",
                email: deptInput.departmentHead.contactEmail != null ? deptInput.departmentHead.contactEmail : ""
            },
            courses: new ArrayList()
        };

        // Handling offered courses
        if (deptInput.offeredCourses != null) {
            for (var j = 0; j < deptInput.offeredCourses.length; j++) {
                var courseInput = deptInput.offeredCourses[j];
                var course = {
                    courseId: courseInput.courseIdentifier != null ? courseInput.courseIdentifier : "",
                    courseName: courseInput.courseTitle != null ? courseInput.courseTitle : "",
                    credits: courseInput.creditHours != null ? courseInput.creditHours : 0,
                    studentsEnrolled: courseInput.enrolledStudents != null ? courseInput.enrolledStudents : 0,
                    schedule: {
                        time: courseInput.classSchedule.classTime != null ? courseInput.classSchedule.classTime : "",
                        location: courseInput.classSchedule.classLocation != null ? courseInput.classSchedule.classLocation : "",
                        days: courseInput.classSchedule.classDays != null ? new ArrayList(courseInput.classSchedule.classDays) : new ArrayList()
                    }
                };
                department.courses.add(course);
            }
        }
        target.university.departments.add(department);
    }
}

// Handling academic staff
if (input.higherEducationInstitution.academicStaff != null) {
    for (var k = 0; k < input.higherEducationInstitution.academicStaff.length; k++) {
        var staffInput = input.higherEducationInstitution.academicStaff[k];
        var faculty = {
            facultyId: staffInput.facultyIdentifier != null ? staffInput.facultyIdentifier : "",
            fullName: staffInput.completeName != null ? staffInput.completeName : "",
            department: staffInput.department != null ? staffInput.department : "",
            email: staffInput.contactEmail != null ? staffInput.contactEmail : "",
            researchAreas: staffInput.researchInterests != null ? new ArrayList(staffInput.researchInterests) : new ArrayList()
        };
        target.university.faculties.add(faculty);
    }
}

// Handling enrolled students
if (input.higherEducationInstitution.enrolledStudents != null) {
    for (var l = 0; l < input.higherEducationInstitution.enrolledStudents.length; l++) {
        var studentInput = input.higherEducationInstitution.enrolledStudents[l];
        var student = {
            studentId: studentInput.studentIdentifier != null ? studentInput.studentIdentifier : "",
            firstName: studentInput.givenName != null ? studentInput.givenName : "",
            lastName: studentInput.surname != null ? studentInput.surname : "",
            major: studentInput.fieldOfStudy != null ? studentInput.fieldOfStudy : "",
            enrollmentYear: studentInput.yearOfEnrollment != null ? studentInput.yearOfEnrollment : 0,
            gpa: studentInput.gradePointAverage != null ? studentInput.gradePointAverage : 0.0,
            coursesEnrolled: studentInput.registeredCourses != null ? new ArrayList(studentInput.registeredCourses) : new ArrayList()
        };
        target.university.students.add(student);
    }
}
